00278         public override void UpdateMeshPadding()
00279         {
00280             m_padding = ShaderUtilities.GetPadding(m_sharedMaterial, m_enableExtraPadding, m_isUsingBold);
00281             m_isMaskingEnabled = ShaderUtilities.IsMaskingEnabled(m_sharedMaterial);
