00371         public override void UpdateVertexData(TMP_VertexDataUpdateFlags flags)
00372         {
00373             int materialCount = m_textInfo.materialCount;
00374 
00375             for (int i = 0; i < materialCount; i++)
00376             {
00377                 Mesh mesh;
00378 
00379                 if (i == 0)
00380                     mesh = m_mesh;
00381                 else
00382                 {
00383                     // Clear unused vertices
00384                     // TODO: Causes issues when sorting geometry as last vertex data attribute get wiped out.
00385                     //m_textInfo.meshInfo[i].ClearUnusedVertices();
00386 
00387                     mesh = m_subTextObjects[i].mesh;
00388                 }
00389 
00390                 //mesh.MarkDynamic();
00391 
00392                 if ((flags & TMP_VertexDataUpdateFlags.Vertices) == TMP_VertexDataUpdateFlags.Vertices)
00393                     mesh.vertices = m_textInfo.meshInfo[i].vertices;
00394 
00395                 if ((flags & TMP_VertexDataUpdateFlags.Uv0) == TMP_VertexDataUpdateFlags.Uv0)
00396                     mesh.uv = m_textInfo.meshInfo[i].uvs0;
00397 
00398                 if ((flags & TMP_VertexDataUpdateFlags.Uv2) == TMP_VertexDataUpdateFlags.Uv2)
00399                     mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
00400 
00401                 //if ((flags & TMP_VertexDataUpdateFlags.Uv4) == TMP_VertexDataUpdateFlags.Uv4)
00402                 //    mesh.uv4 = m_textInfo.meshInfo[i].uvs4;
00403 
00404                 if ((flags & TMP_VertexDataUpdateFlags.Colors32) == TMP_VertexDataUpdateFlags.Colors32)
00405                     mesh.colors32 = m_textInfo.meshInfo[i].colors32;
00406 
00407                 mesh.RecalculateBounds();
00408             }
00409         }
