00196         public override void Rebuild(CanvasUpdate update)
00197         {
00198             if (this == null) return;
00199 
00200             if (update == CanvasUpdate.Prelayout)
00201             {
00202                 if (m_autoSizeTextContainer)
00203                 {
00204                     m_rectTransform.sizeDelta = GetPreferredValues(Mathf.Infinity, Mathf.Infinity);
00205                 }
00206             }
00207             else if (update == CanvasUpdate.PreRender)
00208             {
00209                 OnPreRenderCanvas();
00210 
00211                 m_verticesAlreadyDirty = false;
00212                 m_layoutAlreadyDirty = false;
00213 
00214                 if (!m_isMaterialDirty) return;
00215 
00216                 UpdateMaterial();
00217                 m_isMaterialDirty = false;
00218             }
00219         }
