00244             Material mat = baseMaterial;
00245 
00246             if (m_ShouldRecalculateStencil)
00247             {
00248                 m_stencilID = TMP_MaterialManager.GetStencilID(gameObject);
00249                 m_ShouldRecalculateStencil = false;
