00254             //    MaterialManager.ReleaseStencilMaterial(m_MaskMaterial);
00255 
00256             if (m_stencilID > 0)
00257             {
00258                 mat = TMP_MaterialManager.GetStencilMaterial(baseMaterial, m_stencilID);
00259                 if (m_MaskMaterial != null)
00260                     TMP_MaterialManager.ReleaseStencilMaterial(m_MaskMaterial);
00261 
00262                 m_MaskMaterial = mat;
00263             }
00264 
00265             return mat;
00266         }
