00424         public override void UpdateMeshPadding()
00425         {
00426             m_padding = ShaderUtilities.GetPadding(m_sharedMaterial, m_enableExtraPadding, m_isUsingBold);
00427             m_isMaskingEnabled = ShaderUtilities.IsMaskingEnabled(m_sharedMaterial);
00428             m_havePropertiesChanged = true;
00429             checkPaddingRequired = false;
00430 
00431             // Return if text object is not awake yet.
00432             if (m_textInfo == null) return;
00433 
00434             // Update sub text objects
00435             for (int i = 1; i < m_textInfo.materialCount; i++)
00436                 m_subTextObjects[i].UpdateMeshPadding(m_enableExtraPadding, m_isUsingBold);
00437         }
