00568         public override void UpdateVertexData(TMP_VertexDataUpdateFlags flags)
00569         {
00570             int materialCount = m_textInfo.materialCount;
00571 
00572             for (int i = 0; i < materialCount; i++)
00573             {
00574                 Mesh mesh;
00575 
00576                 if (i == 0)
00577                     mesh = m_mesh;
00578                 else
00579                 {
00580                     // Clear unused vertices
00581                     // TODO: Causes issues when sorting geometry as last vertex data attribute get wiped out.
00582                     //m_textInfo.meshInfo[i].ClearUnusedVertices();
00583 
00584                     mesh = m_subTextObjects[i].mesh;
00585                 }
00586 
00587                 if ((flags & TMP_VertexDataUpdateFlags.Vertices) == TMP_VertexDataUpdateFlags.Vertices)
00588                     mesh.vertices = m_textInfo.meshInfo[i].vertices;
00589 
00590                 if ((flags & TMP_VertexDataUpdateFlags.Uv0) == TMP_VertexDataUpdateFlags.Uv0)
00591                     mesh.uv = m_textInfo.meshInfo[i].uvs0;
00592 
00593                 if ((flags & TMP_VertexDataUpdateFlags.Uv2) == TMP_VertexDataUpdateFlags.Uv2)
00594                     mesh.uv2 = m_textInfo.meshInfo[i].uvs2;
00595 
00596                 //if ((flags & TMP_VertexDataUpdateFlags.Uv4) == TMP_VertexDataUpdateFlags.Uv4)
00597                 //    mesh.uv4 = m_textInfo.meshInfo[i].uvs4;
00598 
00599                 if ((flags & TMP_VertexDataUpdateFlags.Colors32) == TMP_VertexDataUpdateFlags.Colors32)
00600                     mesh.colors32 = m_textInfo.meshInfo[i].colors32;
00601 
00602                 mesh.RecalculateBounds();
00603 
00604                 if (i == 0)
00605                     m_canvasRenderer.SetMesh(mesh);
00606                 else
00607                     m_subTextObjects[i].canvasRenderer.SetMesh(mesh);
00608             }
00609         }
