00010 using UnityEngine;
00011 
00012 namespace Leap.Unity {
00013 
00014   public static class TransformUtil {
00015 
00016     public static Quaternion TransformRotation(this Transform transform, Quaternion rotation) {
00017       return transform.rotation * rotation;
00018     }
00019 
00020     public static Quaternion InverseTransformRotation(this Transform transform, Quaternion rotation) {
00021       return Quaternion.Inverse(transform.rotation) * rotation;
00022     }
00023 
00024     public static void SetLocalX(this Transform transform, float localX) {
00025       transform.setLocalAxis(localX, 0);
00026     }
00027 
00028     public static void SetLocalY(this Transform transform, float localY) {
00029       transform.setLocalAxis(localY, 1);
00030     }
00031 
00032     public static void SetLocalZ(this Transform transform, float localZ) {
00033       transform.setLocalAxis(localZ, 2);
00034     }
00035 
00036     private static void setLocalAxis(this Transform transform, float value, int axis) {
00037       Vector3 local = transform.localPosition;
00038       local[axis] = value;
00039       transform.localPosition = local;
00040     }
00041   }
00042 
00043 }
