00007 using UnityEngine;
00008 using System.Collections;
00009 
00010 namespace Valve.VR.InteractionSystem
00011 {
00012     //-------------------------------------------------------------------------
00013     public class Unparent : MonoBehaviour
00014     {
00015         Transform oldParent;
00016 
00017         //-------------------------------------------------
00018         void Start()
00019         {
00020             oldParent = transform.parent;
00021             transform.parent = null;
00022             gameObject.name = oldParent.gameObject.name + "." + gameObject.name;
00023         }
00024 
00025 
00026         //-------------------------------------------------
00027         void Update()
00028         {
00029             if ( oldParent == null )
00030                 Object.Destroy( gameObject );
00031         }
00032 
00033 
00034         //-------------------------------------------------
00035         public Transform GetOldParent()
00036         {
00037             return oldParent;
00038         }
