00010 using Leap.Unity.Interaction;
00011 using System.Collections;
00012 using System.Collections.Generic;
00013 using UnityEditor;
00014 using UnityEngine;
00015 
00016 namespace Leap.Unity.Examples {
00017 
00018   [CustomEditor(typeof(WorkstationBehaviourExample))]
00019   public class WorkstationBehaviourEditor : CustomEditorBase<WorkstationBehaviourExample> {
00020 
00021     public override void OnInspectorGUI() {
00022       EditorGUI.BeginDisabledGroup(target.workstationModeTween == null
00023                                    || target.workstationModeTween.targetTransform == null
00024                                    || target.workstationModeTween.startTransform == null
00025                                    || target.workstationModeTween.endTransform == null
00026                                    || PrefabUtility.GetPrefabType(target.gameObject) == PrefabType.Prefab);
00027 
00028       EditorGUILayout.BeginHorizontal();
00029 
00030       if (GUILayout.Button(new GUIContent("Open Workstation",
00031                                           "If the workstationModeTween is fully configured, you can "
00032                                         + "press this to set the target transform to the end (open) "
00033                                         + "state."))) {
00034         Undo.IncrementCurrentGroup();
00035         Undo.SetCurrentGroupName("Open Workstation");
00036         Undo.RecordObject(target.workstationModeTween.targetTransform, "Move Target To End");
00037         target.workstationModeTween.SetTargetToEnd();
00038       }
00039 
00040       if (GUILayout.Button(new GUIContent("Close Workstation",
00041                                           "If the workstationModeTween is fully configured, you can "
00042                                         + "press this button to set the target transform to the start "
00043                                         + "(closed) state."))) {
00044         Undo.IncrementCurrentGroup();
00045         Undo.SetCurrentGroupName("Close Workstation");
00046         Undo.RecordObject(target.workstationModeTween.targetTransform, "Move Target To Start");
00047         target.workstationModeTween.SetTargetToStart();
00048       }
00049 
00050       EditorGUILayout.EndHorizontal();
00051 
00052       EditorGUI.EndDisabledGroup();
00053 
00054       base.OnInspectorGUI();
00055     }
00056 
00057   }
00058 
00059 }
