00010 using UnityEngine;
00011 
00012 #if UNITY_2017_2_OR_NEWER
00013 using UnityEngine.XR;
00014 #else
00015 using UnityEngine.VR;
00016 #endif
00017 
00018 namespace Leap.Unity {
00019 
00024   public static class XRSupportUtil {
00025 
00026     public static bool IsXREnabled() {
00027       #if UNITY_2017_2_OR_NEWER
00028       return XRSettings.enabled;
00029       #else
00030       return VRSettings.enabled;
00031       #endif
00032     }
00033 
00034     public static bool IsXRDevicePresent() {
00035       #if UNITY_2017_2_OR_NEWER
00036       return XRDevice.isPresent;
00037       #else
00038       return VRDevice.isPresent;
00039       #endif
00040     }
00041 
00042     static bool outputPresenceWarning = false;
00043     public static bool IsUserPresent(bool defaultPresence = true) {
00044       #if UNITY_2017_2_OR_NEWER
00045       var userPresence = XRDevice.userPresence;
00046       if (userPresence == UserPresenceState.Present) {
00047         return true;
00048       } else if (!outputPresenceWarning && userPresence == UserPresenceState.Unsupported) {
00049         Debug.LogWarning("XR UserPresenceState unsupported (XR support is probably disabled).");
00050         outputPresenceWarning = true;
00051       }
00052       #else
00053       if (!outputPresenceWarning){
00054         Debug.LogWarning("XR UserPresenceState is only supported in 2017.2 and newer.");
00055         outputPresenceWarning = true;
00056       }
00057       #endif
00058       return defaultPresence;
00059     }
00060 
00061     public static Vector3 GetXRNodeCenterEyeLocalPosition() {
00062       #if UNITY_2017_2_OR_NEWER
00063       return InputTracking.GetLocalPosition(XRNode.CenterEye);
00064       #else
00065       return InputTracking.GetLocalPosition(VRNode.CenterEye);
00066       #endif
00067     }
00068 
00069     public static Quaternion GetXRNodeCenterEyeLocalRotation() {
00070       #if UNITY_2017_2_OR_NEWER
00071       return InputTracking.GetLocalRotation(XRNode.CenterEye);
00072       #else
00073       return InputTracking.GetLocalRotation(VRNode.CenterEye);
00074       #endif
00075     }
00076 
00077     public static Vector3 GetXRNodeHeadLocalPosition() {
00078       #if UNITY_2017_2_OR_NEWER
00079       return InputTracking.GetLocalPosition(XRNode.Head);
00080       #else
00081       return InputTracking.GetLocalPosition(VRNode.Head);
00082       #endif
00083     }
00084 
00085     public static Quaternion GetXRNodeHeadLocalRotation() {
00086       #if UNITY_2017_2_OR_NEWER
00087       return InputTracking.GetLocalRotation(XRNode.Head);
00088       #else
00089       return InputTracking.GetLocalRotation(VRNode.Head);
00090       #endif
00091     }
00092 
00093     public static void Recenter() {
00094       InputTracking.Recenter();
00095     }
00096 
00097     public static string GetLoadedDeviceName() {
00098       #if UNITY_2017_2_OR_NEWER
00099       return XRSettings.loadedDeviceName;
00100       #else
00101       return VRSettings.loadedDeviceName;
00102       #endif
00103     }
00104 
00105     public static bool IsRoomScale() {
00106       #if UNITY_2017_2_OR_NEWER
00107       return XRDevice.GetTrackingSpaceType() == TrackingSpaceType.RoomScale;
00108       #else
00109       return VRDevice.GetTrackingSpaceType() == TrackingSpaceType.RoomScale;
00110       #endif
00111     }
00112 
00113     public static float GetGPUTime() {
00114       float gpuTime = 0f;
00115       #if UNITY_5_6_OR_NEWER
00116       #if UNITY_2017_2_OR_NEWER
00117       UnityEngine.XR.XRStats.TryGetGPUTimeLastFrame(out gpuTime);
00118       #else
00119       UnityEngine.VR.VRStats.TryGetGPUTimeLastFrame(out gpuTime);
00120       #endif
00121       #else
00122       gpuTime = UnityEngine.VR.VRStats.gpuTimeLastFrame;
00123       #endif
00124       return gpuTime;
00125     }
00126 
00127   }
