02937 // This is a terrible hack to workaround the fact that VRControllerState_t and VREvent_t were
02938 // originally mis-compiled with the wrong packing for Linux and OSX.
02939     [UnmanagedFunctionPointer(CallingConvention.StdCall)]
02940     internal delegate bool _PollNextOverlayEventPacked(ulong ulOverlayHandle,ref VREvent_t_Packed pEvent,uint uncbVREvent);
02941     [StructLayout(LayoutKind.Explicit)]
02942     struct PollNextOverlayEventUnion
02943     {
02944         [FieldOffset(0)]
02945         public IVROverlay._PollNextOverlayEvent pPollNextOverlayEvent;
02946         [FieldOffset(0)]
02947         public _PollNextOverlayEventPacked pPollNextOverlayEventPacked;
02948     }
02949     public bool PollNextOverlayEvent(ulong ulOverlayHandle,ref VREvent_t pEvent,uint uncbVREvent)
02950     {
02951 #if !UNITY_METRO
02952         if ((System.Environment.OSVersion.Platform == System.PlatformID.MacOSX) ||
02953                 (System.Environment.OSVersion.Platform == System.PlatformID.Unix))
02954         {
02955             PollNextOverlayEventUnion u;
02956             VREvent_t_Packed event_packed = new VREvent_t_Packed();
02957             u.pPollNextOverlayEventPacked = null;
02958             u.pPollNextOverlayEvent = FnTable.PollNextOverlayEvent;
02959             bool packed_result = u.pPollNextOverlayEventPacked(ulOverlayHandle,ref event_packed,(uint)System.Runtime.InteropServices.Marshal.SizeOf(typeof(VREvent_t_Packed)));
02960 
02961             event_packed.Unpack(ref pEvent);
02962             return packed_result;
02963         }
02964 #endif
02965         bool result = FnTable.PollNextOverlayEvent(ulOverlayHandle,ref pEvent,uncbVREvent);
02966         return result;
02967     }
