03230 // This is a terrible hack to workaround the fact that VRControllerState_t and VREvent_t were
03231 // originally mis-compiled with the wrong packing for Linux and OSX.
03232     [UnmanagedFunctionPointer(CallingConvention.StdCall)]
03233     internal delegate bool _GetComponentStatePacked(string pchRenderModelName,string pchComponentName,ref VRControllerState_t_Packed pControllerState,ref RenderModel_ControllerMode_State_t pState,ref RenderModel_ComponentState_t pComponentState);
03234     [StructLayout(LayoutKind.Explicit)]
03235     struct GetComponentStateUnion
03236     {
03237         [FieldOffset(0)]
03238         public IVRRenderModels._GetComponentState pGetComponentState;
03239         [FieldOffset(0)]
03240         public _GetComponentStatePacked pGetComponentStatePacked;
03241     }
03242     public bool GetComponentState(string pchRenderModelName,string pchComponentName,ref VRControllerState_t pControllerState,ref RenderModel_ControllerMode_State_t pState,ref RenderModel_ComponentState_t pComponentState)
03243     {
03244 #if !UNITY_METRO
03245         if ((System.Environment.OSVersion.Platform == System.PlatformID.MacOSX) ||
03246                 (System.Environment.OSVersion.Platform == System.PlatformID.Unix))
03247         {
03248             GetComponentStateUnion u;
03249             VRControllerState_t_Packed state_packed = new VRControllerState_t_Packed(pControllerState);
03250             u.pGetComponentStatePacked = null;
03251             u.pGetComponentState = FnTable.GetComponentState;
03252             bool packed_result = u.pGetComponentStatePacked(pchRenderModelName,pchComponentName,ref state_packed,ref pState,ref pComponentState);
03253 
03254             state_packed.Unpack(ref pControllerState);
03255             return packed_result;
03256         }
03257 #endif
03258         bool result = FnTable.GetComponentState(pchRenderModelName,pchComponentName,ref pControllerState,ref pState,ref pComponentState);
03259         return result;
03260     }
