06283     public static CVRSystem Init(ref EVRInitError peError, EVRApplicationType eApplicationType = EVRApplicationType.VRApplication_Scene, string pchStartupInfo= "")
06284     {
06285         try
06286         {
06287             VRToken = InitInternal2(ref peError, eApplicationType, pchStartupInfo);
06288         }
06289         catch (EntryPointNotFoundException)
06290         {
06291             VRToken = InitInternal(ref peError, eApplicationType);
06292         }
06293 
06294         OpenVRInternal_ModuleContext.Clear();
06295 
06296         if (peError != EVRInitError.None)
06297             return null;
06298 
06299         bool bInterfaceValid = IsInterfaceVersionValid(IVRSystem_Version);
06300         if (!bInterfaceValid)
06301         {
06302             ShutdownInternal();
06303             peError = EVRInitError.Init_InterfaceNotFound;
06304             return null;
06305         }
06306 
06307         return OpenVR.System;
06308     }
06309 
06312     public static void Shutdown()
06313     {
06314         ShutdownInternal();
06315     }
